export type SDKEventType = 'READY' | 'ELIGIBILITY_RESULT' | 'ERROR' | 'CLOSE' | 'INITIATED' | 'CLOSE_FRAME' | 'JOURNEY_STEP_COMPLETED' | 'SESSION_CLEARED' | 'SDK_REINITIALIZED' | 'REINITIALIZATION_FAILED';
export type SDKMode = 'popup' | 'inline' | 'embedded';
export interface SDKEventData {
    sessionId?: string;
    themeConfig?: Record<string, unknown>;
    error?: SDKError;
    result?: {
        status: string;
        data: Record<string, unknown>;
    };
    completedStep?: string;
    nextStep?: string;
    stepSummary?: {
        totalSteps: number;
        completedSteps: number;
        currentStep: string | null;
        nextStep: string | null;
        completionPercentage: number;
    };
    reason?: string;
    mobileNumber?: string;
    timestamp?: string;
}
export interface SDKEvent {
    type: SDKEventType;
    data: SDKEventData;
}
export interface SDKError {
    code: string;
    message: string;
}
export type EventHandler = (event: SDKEvent) => void;
export interface LoanEligibilitySDK {
    initialize(config: PartnerConfig): Promise<void>;
    openEligibilityCheck(mode: SDKMode): Promise<void>;
    close(): void;
    on(event: string, handler: EventHandler): void;
    off(event: string, handler: EventHandler): void;
}
