// SDK URLs - Update these based on your environment
export const SDK_CONFIG = {
  production: {
    SDK_URL: 'https://sdk-lark.larkfinserv.in',
    SDK_API_URL: 'https://backend-sdk.larkfinserv.in',
  },
  sandbox: {
    SDK_URL: 'https://sdk-lark.larkfinserv.in',
    SDK_API_URL: 'https://backend-sdk.larkfinserv.in',
  },
};

export const DEFAULT_HEADERS = {
  'Content-Type': 'application/json',
  'ngrok-skip-browser-warning': 'true',
};

export const getSDKUrl = (environment: 'sandbox' | 'production' = 'production'): string => {
  return SDK_CONFIG[environment].SDK_URL;
};

export const getSDKApiUrl = (environment: 'sandbox' | 'production' = 'production'): string => {
  return SDK_CONFIG[environment].SDK_API_URL;
};
